import React,{useEffect,useState} from 'react';
import VideoRoom from '../../components/VideoRoom/VideoRoom'
import axios from 'axios';


const RoomPage = () => {
  const [rooms, setRooms] = useState([])

  const fetchAllRoom = () => {
    axios.get('http://localhost:5000/lp', {headers: {'Access-Control-Allow-Origin': '*'}})
      .then(response => {
        setRooms(response.data)
        console.log('Loai Phong',response.data)
        console.log('Loai Phong',response.data.$values)
      })
  }

  useEffect(() =>{
    fetchAllRoom()
  }, [])

  return (
    <>
      <section class="site-section">
      <div class="container">
        <div class="row">
          <div class="col-md-4 mb-4">
            <div class="media d-block room mb-0">
              <figure>
                <img src="images/img_2.jpg" alt="Generic placeholder image" class="img-fluid"/>
                {/* item.anh */}
                <div class="overlap-text">
                  <span>
                    Featured Room
                    <span class="ion-ios-star"></span>
                    <span class="ion-ios-star"></span>
                    <span class="ion-ios-star"></span>
                  </span>
                </div>
              </figure>
              <div class="media-body">
                <h3 class="mt-0"><a href="#">Presidential Room</a></h3>           {/* item.tenphong */}
                <ul class="room-specs">
                  <li><span class="ion-ios-people-outline"></span> 2 Guests</li>  {/* item.thongtinphong */}
                  <li><span class="ion-ios-crop"></span> 22 ft <sup>2</sup></li>  {/* item.dientich */}
                </ul>
                <p>Nulla vel metus scelerisque ante sollicitudin. Fusce condimentum nunc ac nisi vulputate fringilla. </p> {/* item.mota */}
                <p><a href="#" class="btn btn-primary btn-sm">Book Now From $20</a></p>  {/* item.giaphong */}
              </div>
            </div>
          </div>
          
        </div>
      </div>
    </section>
    <VideoRoom/>
    </>
  );
};

export default RoomPage;
