import React,{useState,useEffect} from 'react';
import { Form, Input, Button, Checkbox } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import {
  Link
} from "react-router-dom";
import 'antd/dist/antd.css'
import axios from 'axios';

import { useSelector, useDispatch } from 'react-redux'
import { setUser as setUserStore } from './../../redux/reducers/user'
import HomePage from '../HomePage/HomePage'

const SignInPage = () => {
  const styleSize = {
    paddingLeft: '47px',
    display: 'flex',
    color: 'black',
    fontSize: '30px',
    justifyContent: 'center',
    marginTop: '40px',
    marginBottom: '9px'
  }

  const tailLayout = {
    wrapperCol: {
      offset: 10,
      span: 5,
    },
  };

  const layout = {
    labelCol: {
      span: 10,
    },
    wrapperCol: {
      span: 5,
    },
  };
  const [user,setUser] = useState([]);
  const loggedInUser = useSelector(store => store.user.user)
  const dispatch = useDispatch()

  useEffect(() => {
    console.log('----loggedInUser:', loggedInUser)
  }, [loggedInUser])

  const onFinish = async (values) => {
    console.log('success', values)
    await axios.post(`http://localhost:5000/account/signin?email=${values.email}&password=${values.password}`, {
      ...values
    })
      .then(response => {
        console.log('data',response.data)
        
        if(response.data){
          // dispatch(setUserStore({
          //   name: user[0].TenKH,
          //   email: values.email
          // }))
          console.log('ban da dang nhap thanh cong')
        }else{
           console.log('bqweqwehap thaqweg')
  
        }
        
        setUser(response.data)
        // dispatch(setUserStore({
        //   name: response.data[0].TenKH,
        //   email: values.email,
        //   loggedIn:true
        // }))
      })

      
      // if (user) {
      //   console.log('ban da dang nhap thanh cong')
      // }
      // else{
      //   console.log('non')
      // }
  }

  // useEffect(()=>{
  //   setUser();
  // },[])

  const onFinishFailed = (errors) => {
    console.log('unsuccess', errors)
  }
  return (
    <>
    
     <div>{ loggedInUser ? <div>Logout</div>: <HomePage/>  } </div> 
    
    <span style={styleSize}>ĐĂNG NHẬP</span>
      <Form
        {...layout}
        name="normal_login"
        className="login-form"
        initialValues={{
          remember: true,
        }}
        onFinish={onFinish}
      >
        <Form.Item {...tailLayout}
          name="email"
          rules={[
            {
              required: true,
              message: 'Please input your email!',
            },
          ]}
        >
          <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Email" />
        </Form.Item>
        <Form.Item {...tailLayout}
          name="password"
          rules={[
            {
              required: true,
              message: 'Please input your Password!',
            },
          ]}
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            type="password"
            placeholder="Password"
          />
        </Form.Item>
        <Form.Item {...tailLayout}>
          <Form.Item name="remember" valuePropName="checked" noStyle>
            <Checkbox>Remember me</Checkbox>
          </Form.Item>
        </Form.Item>

        <Form.Item {...tailLayout}>
          <Button type="primary" htmlType="submit" className="login-form-button">
            Đăng Nhập
        </Button>
        Hoặc <Link to="/signup">Đăng Ký Ngay</Link>
        </Form.Item>
      </Form>
    </>
  );
};

export default SignInPage;
